#include "issueMethods.h"

using namespace std;

//Global Variables
map <int ,Issue> issueMap;
ofstream file;

/*------Allexa's random Code-------*/
int randomID(){
        return (rand() % 899999) + 100000;
}

/*---Tim's code---*/
//writing to file
void createIssue() {
    //temp values
    string name;
    //ID will be generated by another function
    string desc;
    string priority;
    string status;

    //ask user to enter bug info
    cout << "Enter a name for the bug: ";
    getline(cin, name);
    cout << "Enter a description: ";
    getline(cin, desc);
    cout << "Enter a priority: ";
    getline(cin, priority);
    cout << "Enter a status: ";
    getline(cin, status);

    //random ID assigned
    int ID = randomID();
    //creating map here
    createIssueMap(ID, name, desc, status, priority);

    //write to file
    file << name << ",";       //Name
    file << ID << ",";         //ID
    file << desc << ",";       //Description
    file << priority << ",";   //Priority
    file << status << "\n";     //Completion status
}

void createIssuesFile(){
    //creating file
    file.open("buglist1.csv");

    file << "NAME,"; //Name
    file << "ID #,"; //ID
    file << "DESCRIPTION,"; //Description
    file << "PRIORITY,"; //Priority
    file << "STATUS\n"; //Completion status
    string x;

    //FOR TESTING
    while(x != "no"){
    createIssue();
    cout << "Keep adding issues? yes/no" << endl;
    getline(cin, x);
    }
    cout << endl;

    file.close();
}

/*---Jaime's Code---*/

void createIssueMap(int id, string name, string descrip, string status, string prio){

    //Identifies duplicates and re-rolls
    if(issueMap.count(id) > 0) {
        id = randomID();
    }

    issueMap.insert(pair<int, Issue>(id, *new Issue(id, name, descrip, status, prio)));
}

void displayAllIssues(){
    cout << endl;
    for(auto x : issueMap) {
    x.second.displayIssue();
    cout << endl;
    }
}

void issueMenu(){
    string input;
    string input2;

    cout << "---Main Menu---" << endl;
    cout << "Enter 1-3 to select an option or 0 to exit" << endl;
    cout << "1: Display all issues" << endl;
    cout << "2: Display specific issue" << endl;
    cout << "3: Create an issue report" << endl;
    
    cin >> input;
    cin.ignore();
    
    if(input == "0")
        return;
    else if(input == "1")
        displayAllIssues();
    else if(input == "2")
        displaySpecificIssue();
    else if(input == "3")
        createIssuesFile();
    else {
        cout << "Invalid entry try again" << endl;
        cout << endl;
        issueMenu();
        return;
    }

    cout << "Anything else? y/n" << endl;
    getline(cin, input2);

    if(input2 == "y")
        issueMenu();
    else if(input2 == "yes")
        issueMenu();
    else
        cout << "Bye Bye" << endl;
        return;
}

/*------Allexa's Code-------*/

void displaySpecificIssue() {
    //Ask for User Specification of Input
    int x;
    cout << "1. Search by word or phrase" << endl;
    cout << "2. Search by ID" << endl;
    cout << "Enter search option: ";
    cin >> x;
    
    //Decides on what search method to use
    if (x == 1) {
        string search;
        cout << "Enter search term: ";
        cin >> search;
        cout << endl;
        
        //Search by string
        for(auto x : issueMap) {
            x.second.searchTerm(search);
        }
    }
    else if (x == 2) {
        int search;
        cout << "Enter search ID: ";
        cin >> search;
        cout << endl;
        
        //Search by int ID
        for(auto x : issueMap) {
            x.second.searchID(search);
        }
    }
    else {
        //Error catching
        cout << "Not a valid input" << endl;
    }
}
